<script setup>
import { ipcRenderer } from 'electron'
import { useStore } from 'vuex'
import { ref, computed, onMounted, onUnmounted, nextTick } from 'vue'
import { ModuleName, Method, Type, ViewName } from '@/constant'
import MarkerItem from '@/components/MarkerItem.vue'

const store = useStore()

// const common = computed(() => store.state.common)
// const recordingTime = ref(
//   common.value.find((one) => one.key === '탐색 녹화시간')
// )

const directionOption = computed(() => store.state.directionOption)
const locationTable = computed(() => store.state.location)

const navigationImage = ref('')
const cameraImage = ref('')

const videoPath = ref('')

const intervalIndex = ref(0)
const selectedIndex = ref(-1)
const locationName = ref('')
const videoCommands = ['영상 시작', '영상 정지', '영상 중지', '영상 종료']
const title = ref('탐색')

const ctx = ref(null)
const mapCanvas = ref(null)

const videoRef = ref(null)
let intervalId = null

const isLoading = ref(false)
const isVideoPlaying = ref(false)

const mapImage = require('@/assets/image/isol_map.jpg')

const Modes = {
  INITIAL: 'INITIAL',
  SEARCHING: 'SEARCHING',
  SEARCH_COMPLETE: 'SEARCH_COMPLETE',
}

const currentMode = ref(Modes.INITIAL)
const isRecording = ref(false)

onMounted(() => {
  const canvas = document.createElement('canvas')
  mapCanvas.value = canvas
  ctx.value = canvas.getContext('2d')

  const mapImg = new Image()
  mapImg.onload = () => {
    canvas.width = mapImg.width
    canvas.height = mapImg.height
    ctx.value.drawImage(mapImg, 0, 0)
  }
  mapImg.src = mapImage

  ipcRenderer.on(ModuleName.POST_OFFICE, handlePostOfficeMessage)
  startInterval('marker')

  //   setTimeout(() => {
  //     const x = Math.floor(Math.random() * 542)
  //     const y = Math.floor(Math.random() * 434)
  //     const radian = Math.random() * 2 * Math.PI
  //     const testData = `${x},${y},${radian}`
  //     drawCoordinatesOnMap(testData)
  //     isLoading.value = false
  //   }, 10000)

  //   setTimeout(() => {
  //     const test = {
  //       method: Method.POST,
  //       type: Type.SEARCH,
  //       content: { data: 'record', error: null },
  //     }
  //     handlePostOfficeMessage(null, test)
  //   }, 15000)

  //   setTimeout(() => {
  //     const test = {
  //       method: Method.POST,
  //       type: Type.SEARCH,
  //       content: { data: 'find_human', error: null },
  //     }
  //     handlePostOfficeMessage(null, test)
  //   }, 20000)

  setInterval(() => {
    const testImage =
      ''
    const test = {
      method: Method.POST,
      type: Type.CAMERA_IMAGE,
      content: { data: testImage, error: false },
    }
    handlePostOfficeMessage(null, test)
  }, 28)

  //   setTimeout(() => {
  //     const x = Math.floor(Math.random() * 542)
  //     const y = Math.floor(Math.random() * 434)
  //     const radian = Math.random() * 2 * Math.PI
  //     const testData = `${x},${y},${radian}`
  //     drawCoordinatesOnMap(testData)
  //     isLoading.value = false
  //   }, 25000)
})

onUnmounted(() => {
  ipcRenderer.off(ModuleName.POST_OFFICE, handlePostOfficeMessage)
  clearInterval(intervalId)
})

function handlePostOfficeMessage(sender, payload) {
  console.log(payload)
  if (payload.method === Method.POST && payload.type === Type.POSITION) {
    const location = locationTable.value.find(
      (marker) => marker.position === payload.content.data
    )

    if (location) {
      locationName.value = location.position
      currentMode.value = Modes.SEARCHING
      isLoading.value = true
      title.value = '탐색 중 - ' + locationName.value
      selectedIndex.value = locationTable.value.findIndex(
        (marker) => marker.position === payload.content.data
      )

      const [x, y] = location.coordinates.split(',').map(Number)
      const direction = directionOption.value.find(
        (option) => option.id === location.direction_option_id
      )
      const degree = direction ? parseInt(direction.val) : 0
      const radian = (degree * Math.PI) / 180

      ipcRenderer.send(ModuleName.WEB_APPLICATION, {
        method: Method.GET,
        type: Type.SEARCH,
        content: {
          data: `${x},${y},${radian},${30}`,
          error: false,
        },
      })

      clearInterval(intervalId)
    }
  } else if (
    payload.method === Method.POST &&
    payload.type === Type.NAVIGATION
  ) {
    if (!payload.content.error) {
      isLoading.value = false
      const data = payload.content.data
      drawCoordinatesOnMap(data)
    } else {
      // handleError()
    }
  } else if (
    payload.method === Method.POST &&
    payload.type === Type.CAMERA_IMAGE
  ) {
    if (!payload.content.error) {
      isLoading.value = false
      updateCameraImage(`data:image/png;base64,${payload.content.data}`)
    } else {
      // handleError()
    }
  } else if (payload.method === Method.POST && payload.type === Type.SEARCH) {
    if (payload.content.data === 'record') {
      isRecording.value = true
      title.value = '녹화 중 - ' + locationName.value
      resetDrawing()
    } else if (payload.content.data === 'find_human') {
      isRecording.value = false
      selectedIndex.value = null
      title.value = '사용자에게 돌아가는 중'
      resetDrawing()
    } else if (payload.content.data === 'fail') {
      const locationsData = locationTable.value.map((location) => {
        const [x, y] = location.coordinates.split(',').map(Number)
        const direction = directionOption.value.find(
          (option) => option.id === location.direction_option_id
        )
        const degree = direction ? parseInt(direction.val) : 0
        const radian = (degree * Math.PI) / 180
        return `${x},${y},${radian}`
      })

      ipcRenderer.send(ModuleName.WEB_APPLICATION, {
        method: Method.POST,
        type: 'FIND_HUMAN',
        content: { data: locationsData, error: null },
      })
    }
  } else if (payload.method === Method.GET && payload.type === Type.SEARCH) {
    if (!payload.content.error) {
      videoPath.value = require('@/assets/image/KakaoTalk_20240621_133554776.mp4')
      isLoading.value = false
      currentMode.value = Modes.SEARCH_COMPLETE
      title.value = '탐색 후 - ' + locationName.value
      intervalIndex.value = 0
      startInterval('video')
    } else {
      // handleError()
    }
  } else if (payload.method === Method.POST && payload.type === Type.COMMAND) {
    handleCommand(payload.content.data)
  }
}

function updateCameraImage(src) {
  nextTick(() => {
    cameraImage.value = src
  })
}
function drawCoordinatesOnMap(data) {
  const [x, y, radian] = data.split(',').map(Number)
  radian
  ctx.value.fillStyle = 'red'
  ctx.value.beginPath()
  ctx.value.arc(x, y, 5, 0, 2 * Math.PI)
  ctx.value.fill()

  navigationImage.value = mapCanvas.value.toDataURL()
}

function resetDrawing() {
  // 캔버스를 초기화
  ctx.value.clearRect(0, 0, mapCanvas.value.width, mapCanvas.value.height)

  // 새로운 이미지를 로드하여 그리기
  const mapImg = new Image()
  mapImg.onload = () => {
    ctx.value.drawImage(mapImg, 0, 0)
    // 초기화된 캔버스를 navigationImage에 업데이트
    navigationImage.value = mapCanvas.value.toDataURL()
  }
  mapImg.src = mapImage
}

// function handleError() {
//   resetToInitialState()
//   store.dispatch('showErrorDialog', {
//     message: '오류가 발생했습니다. 다시 시작해주세요',
//     duration: 3000,
//   })
// }

function resetToInitialState() {
  isLoading.value = false
  currentMode.value = Modes.INITIAL
  title.value = '탐색'
  startInterval('marker')
}

function handleCommand(command) {
  if (currentMode.value === Modes.SEARCHING) return

  switch (command) {
    case 'video_start':
      startVideo()
      break
    case 'video_pause':
      pauseVideo()
      break
    case 'video_stop':
      stopVideo()
      break
    case 'video_exit':
      exitVideo()
      break
    default:
      console.error(`Unsupported command: ${command}`)
  }
}

function startVideo() {
  if (videoRef.value) {
    videoRef.value.play()
    isVideoPlaying.value = true
  }
}

function pauseVideo() {
  if (videoRef.value) {
    videoRef.value.pause()
    isVideoPlaying.value = false
  }
}

function stopVideo() {
  if (videoRef.value) {
    videoRef.value.pause()
    videoRef.value.currentTime = 0
    isVideoPlaying.value = false
  }
}

function exitVideo() {
  resetToInitialState()
}

function startInterval(type) {
  clearInterval(intervalId)
  intervalId = setInterval(() => {
    if (type === 'marker') {
      intervalIndex.value =
        (intervalIndex.value + 1) % locationTable.value.length
    } else if (type === 'video') {
      intervalIndex.value = (intervalIndex.value + 1) % videoCommands.length
    }
  }, 3000)
}

function getRotationDegree(marker) {
  const directionOptionItem = directionOption.value.find(
    (option) => option.id === marker.direction_option_id
  )
  return directionOptionItem ? parseInt(directionOptionItem.val) : 0
}
</script>

